package com.example.backend.admin.service.impl.sys;import com.example.backend.admin.auth.hash.MD5HashServiceImpl;import com.example.backend.admin.auth.jwt.JWTAuthenticalServiceImpl;import com.example.backend.admin.dao.ISysCartDao;import com.example.backend.admin.dao.ISysUserDao;import com.example.backend.admin.service.ISysService;import com.example.common.base.Cred;import com.example.common.base.GTException;import com.example.common.domain.sys.SysCart;import com.example.common.domain.sys.SysOrder;import com.example.common.domain.sys.SysUser;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Service@Transactionalpublic class SysServiceImpl implements ISysService {    @Autowired    private ISysUserDao sysUserDao;    @Autowired    private MD5HashServiceImpl md5HashService;    @Autowired    private JWTAuthenticalServiceImpl jwtAuthenticalService;    @Autowired    private ISysCartDao sysCartDao;    @Override    public List<SysUser> sysUserGetList(Cred cred, SysUser user) throws Exception {        return sysUserDao.getlist(cred, user);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void sysUserInser(Cred cred, SysUser user) throws Exception {        sysUserDao.insert(cred, user);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public String sysUserLogin(Cred cred, SysUser user) throws Exception {        SysUser sysUser = sysUserDao.getByUserID(cred, user.getSysUserId());        if (sysUser.getSysUserId().isEmpty()) {            throw new GTException("Tài khoản không đúng", null, null);        }        String md5Pass = md5HashService.hashMD5(user.getPassword());        if (sysUser.getPassword().compareTo(md5Pass) != 0) {            throw new GTException("Mật khẩu không đúng", null, null);        }        return jwtAuthenticalService.generateToken(cred);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void sysUserDelete(Cred cred, SysUser user) throws Exception {        sysUserDao.delete(cred, user);    }    @Override    public List<SysCart> getSysCart(Cred cred, SysCart obj) throws Exception {        return sysCartDao.get(cred, obj);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void insertSysCart(Cred cred, SysCart obj) throws Exception {        sysCartDao.insert(cred, obj);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void updateSysCart(Cred cred, SysCart obj) throws Exception {        sysCartDao.update(cred, obj);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void deleteSysCart(Cred cred, SysCart obj) throws Exception {        sysCartDao.delete(cred, obj);    }    @Transactional(rollbackFor = { Exception.class })    @Override    public void orderSysCart(Cred cred, List<SysOrder> obj) throws Exception {        for (SysOrder order: obj) {            sysCartDao.order(cred, order);        }    }}